santa_floor :: [Char] -> Int
santa_floor [] = 0
santa_floor (x : xs)
    | x == '(' = 1 + santa_floor xs
    | x == ')' = -1 + santa_floor xs
    | otherwise = santa_floor xs

check_basement :: [Char] -> Int -> Int -> Int
check_basement [] _ _ = 0
check_basement (x : xs) current_floor position
    | x == ')' && current_floor == 0 = position + 1
    | x == ')' = check_basement xs (current_floor - 1) (position + 1)
    | x == '(' = check_basement xs (current_floor + 1) (position + 1)
    | otherwise = check_basement xs current_floor (position + 1)


